import React, { useState } from 'react';
import Switch from "./Switch";

function App() {
  const [name, setName] = useState(''); // State to hold the name

  const handleNameChange = (e) => {
    setName(e.target.value); // Update the name on input change
  };

  return (
    <div className="app">
      {/* Name input field */}
      <h2>Name:</h2>
      <input
        type="text"
        value={name}
        onChange={handleNameChange}
        placeholder="Enter your name"
      />

      {/* Render role options */}
      <h1>Sub/Dom <Switch /></h1>
      <h1>Roleplay <Switch /></h1>
      <h1>Pictures <Switch /></h1>
      <h1>Videos <Switch /></h1>
      <h1>Challenges <Switch /></h1>

      {/* Submit button */}
      <button onClick={() => alert(`Name: ${name}`)}>Submit</button>
    </div>
  );
}

export default App;
