import React, { useState } from 'react';
import { createClient } from '@supabase/supabase-js';
import Switch from "./Switch";

// Initialize Supabase client
const supabase = createClient('https://your-supabase-url', 'your-anon-key');

function App() {
  const [name, setName] = useState('');
  const [subDom, setSubDom] = useState(false);
  const [roleplay, setRoleplay] = useState(false);
  const [pictures, setPictures] = useState(false);
  const [videos, setVideos] = useState(false);
  const [challenges, setChallenges] = useState(false);

  const handleNameChange = (e) => setName(e.target.value);

  const handleSubmit = async () => {
    const { data, error } = await supabase
      .from('user_preferences')
      .upsert([
        {
          name: name,
          sub_dom: subDom,
          roleplay: roleplay,
          pictures: pictures,
          videos: videos,
          challenges: challenges
        }
      ]);

    if (error) {
      alert("Error updating preferences");
      console.error(error);
    } else {
      alert(`Successfully updated preferences for ${name}`);
    }
  };

  return (
    <div className="app">
      <h2>Name:</h2>
      <input
        type="text"
        value={name}
        onChange={handleNameChange}
        placeholder="Enter your name"
      />

      <h1>Sub/Dom <Switch checked={subDom} onChange={setSubDom} /></h1>
      <h1>Roleplay <Switch checked={roleplay} onChange={setRoleplay} /></h1>
      <h1>Pictures <Switch checked={pictures} onChange={setPictures} /></h1>
      <h1>Videos <Switch checked={videos} onChange={setVideos} /></h1>
      <h1>Challenges <Switch checked={challenges} onChange={setChallenges} /></h1>

      <button onClick={handleSubmit}>Submit</button>
    </div>
  );
}

export default App;
